parameters.inputSchema:
{
  "type": "object",
  "required": ["news_articles"],
  "properties": {
    "news_articles": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "title": {"type": "string"},
          "content": {"type": "string"},
          "source": {"type": "string"},
          "date": {"type": "string"}
        }
      },
      "description": "List of news articles with title, content, source, and date fields"
    }
  }
}

{
  "type": "object",
  "required": ["social_posts"],
  "properties": {
    "social_posts": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "text": {"type": "string"},
          "platform": {"type": "string", "enum": ["twitter", "reddit", "linkedin"]},
          "timestamp": {"type": "string"},
          "engagement": {"type": "number"},
          "likes": {"type": "number"},
          "retweets": {"type": "number"},
          "replies": {"type": "number"}
        }
      },
      "description": "List of social media posts with text, platform, and engagement metrics"
    }
  }
}
